import React, { useState } from 'react';
import '../App.css';

const Nav = () => {
  const [activeTab, setActiveTab] = useState(1);

  const handleTabClick = (tabIndex) => {
    setActiveTab(tabIndex);
  };

  let textBoxContent;
  switch (activeTab) {
    case 1:
      textBoxContent = "Content for Tab 1";
      break;
    case 2:
      textBoxContent = "Content for Tab 2";
      break;
    case 3:
      textBoxContent = "Content for Tab 3";
      break;
    default:
      textBoxContent = "";
  }

  return (
    <div className="container">
      <div className="title">
        Oval Text
      </div>
      <div className={`navOval ${activeTab === 1 ? 'active' : ''}`} onClick={() => handleTabClick(1)}>Tab 1</div>
      <div className={`navOval ${activeTab === 2 ? 'active' : ''}`} onClick={() => handleTabClick(2)}>Tab 2</div>
      <div className={`navOval ${activeTab === 3 ? 'active' : ''}`} onClick={() => handleTabClick(3)}>Tab 3</div>
      <div className="textBox">{textBoxContent}</div>
    </div>
  );
};

export default Nav;
