import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // Import Link from React Router
import '../App.css';

const Nav = () => {
  const [activeTab, setActiveTab] = useState(1);

  const handleTabClick = (tabIndex) => {
    setActiveTab(tabIndex);
  };

  let textBoxContent;
  switch (activeTab) {
    case 1:
      textBoxContent = "Content for Tab 1";
      break;
    case 2:
      textBoxContent = "Content for Tab 2";
      break;
    case 3:
      textBoxContent = "Content for Tab 3";
      break;
    default:
      textBoxContent = "";
  }

  return (
    <div className="container">
      <div className="title-container">
        <div className="title">
          Amy F
        </div>
        <Link to="/tab1" className={`navOval ${activeTab === 1 ? 'active' : ''}`} onClick={() => handleTabClick(1)}>
          <span>Tab 1</span>
        </Link>
        <Link to="/tab2" className={`navOval ${activeTab === 2 ? 'active' : ''}`} onClick={() => handleTabClick(2)}>
          <span>Tab 2</span>
        </Link>
        <Link to="/tab3" className={`navOval ${activeTab === 3 ? 'active' : ''}`} onClick={() => handleTabClick(3)}>
          <span>Tab 3</span>
        </Link>
      </div>
      <div className="textBox">
        <div className="textBox-content">{textBoxContent}</div>
      </div>
    </div>
  );
};

export default Nav;
